mapper.select:
select u.user_id, u.dept_id, u.user_name,u.middle_name, u.external_email,u.internal_email, u.avatar, u.given_name, u.family_name, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark,u.graduation_year,u.duration_of_qualify,u.counselor_id, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')
AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name,u.middle_name, u.external_email, u.internal_email, u.given_name, u.family_name, u.status, u.create_time,u.graduation_year,u.duration_of_qualify,u.counselor_id from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name,u.middle_name, u.external_email, u.internal_email,u.given_name, u.family_name, u.status, u.create_time,u.student_id,u.graduation_year,u.duration_of_qualify,u.counselor_id from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and (r.role_id != #{roleId} or r.role_id IS NULL) and u.user_id not in (select u.user_id from sys_user u inner join sys_user_role ur on u.user_id = ur.user_id and ur.role_id = #{roleId})
AND u.user_name like concat('%', #{userName}, '%')
${params.dataScope}

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, middle_name = #{middleName}, external_email = #{externalEmail}, internal_email = #{internalEmail}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, graduation_year = #{graduationYear}, duration_of_qualify = #{durationOfQualify}, counselor_id = #{counselorId}, family_name = #{familyName}, given_name = #{givenName}, update_time = sysdate()
where user_id = #{userId}

update sys_user
set status = #{status}
where user_id = #{userId}

update sys_user
set avatar = #{avatar}
where user_name = #{userName}

update sys_user
set password = #{password}
where user_name = #{userName}

mapper.delete:
update sys_user
set del_flag = '2'
where user_id = #{userId}
